package com.emotiv.dao;import com.emotiv.insight.IEdk;import android.content.Context;public class EngineConnector {    private Context context;    private static EngineConnector engineInstance = null;	public static EngineConnector shareInstance(Context context) {		if (engineInstance == null) {			engineInstance = new EngineConnector(context);		}		return engineInstance;	}	private EngineConnector(Context context) {        this.context = context;		connectEngine();	}	private void connectEngine() {		/*		 * Inicializa a conexão com o EmoEngine, após isso o		 * software está preparado para fazer qualquer requisição		 * ao EmoEngine		 */		 IEdk.IEE_EngineConnect(this.context, "");	}}